String loginService = "";
String submit = "";
String esteira = testRunner.testCase.testSuite.project.getPropertyValue("Esteira");
String somEsteira = "";
String updateCPEBB = "";
String updateCPE = "";


if (esteira == "1"){
	somEsteira = "som18Esteira1";	
	updateCPE = "urEsteira1";
}else if(esteira == "2"){
	somEsteira = "som17Esteira2";	
	updateCPE = "urEsteira2";
}else if(esteira.toUpperCase() == "PREPROD"){
	somEsteira = "som19PreProd";
	updateCPE = "urPreProd";
}else{
	somEsteira = "";
	updateCPE = ""
	com.eviware.soapui.support.UISupport.showInfoMessage('Favor inserir ESTEIRA como: 1 (Eteira 1), 2 (Esteira 2) ou preprod (Pré-Prod)');
	testRunner.cancel("Favor inserir ESTEIRA como: 1 (Eteira 1), 2 (Esteira 2), ou preprod (Pré-Prod)");
}

if (somEsteira != ""){
	loginService = testRunner.testCase.testSuite.project.getPropertyValue(somEsteira) + ":20100/dop/security/LoginService";	
	submit = testRunner.testCase.testSuite.project.getPropertyValue(somEsteira) + ".redecorp.br:20100/vivo-services/NotifyResponseWSPort";
	updateCPEBB = testRunner.testCase.testSuite.project.getPropertyValue(updateCPE) + "/Resource/ResourceInventoryManagement/AllocateAndInstallResource";
}
testRunner.testCase.testSteps['PropriedadeCod'].setPropertyValue('endpointLoginService', loginService );
testRunner.testCase.testSteps['PropriedadeCod'].setPropertyValue('endpointSubmit', submit );
testRunner.testCase.testSteps['PropriedadeCod'].setPropertyValue('endpointUpdate', updateCPEBB );

log.info(somEsteira)
log.info(loginService)
log.info(submit)
log.info(updateCPEBB)

