import org.apache.commons.lang.RandomStringUtils;
String macAddressInternet = "";
for (int i=0; i<=5; i++){
	String auxMacAddressInternet = RandomStringUtils.randomAlphanumeric(2);
	if(i<5){
		macAddressInternet = auxMacAddressInternet + ":" + macAddressInternet;
	} else{
		macAddressInternet = macAddressInternet + auxMacAddressInternet;
	}
}
macAddressInternet = macAddressInternet.toUpperCase();
testRunner.testCase.testSteps['PropertyRandominco'].setPropertyValue('RamdomicoInternetMacAddress', (String)macAddressInternet );

String macAddressTV = "";
for (int i=0; i<=5; i++){
	String auxMacAddressTV = RandomStringUtils.randomAlphanumeric(2);
	if(i<5){
		macAddressTV = auxMacAddressTV + ":" + macAddressTV;
	} else{
		macAddressTV = macAddressTV + auxMacAddressTV;
	}
}
macAddressTV = macAddressTV.toUpperCase();
testRunner.testCase.testSteps['PropertyRandominco'].setPropertyValue('RandomicoTVMacAddress', (String)macAddressTV );

